mapper.insert:

    SELECT COALESCE(MAX(SEQ) + 1, 1) AS SEQ FROM EXERCISE_DETAIL

INSERT INTO EXERCISE_DETAIL(
    SEQ
    , CODE
    , SET_NUMBER
    , TIMES
    , WEIGHT
    , EXERCISE_DATE
)
VALUES (
    #{seq}
    , #{code}
    , #{setNumber}
    , #{times}
    , #{weight}
    , #{exerciseDate}
)
